        <script>
       function IDalex(){
       var playid = $("#playidm").val();
       var zoneid = $("#playzonem").val();
       $(".respon").show();
       $(".wrong").hide();
       $(".tidakada").hide();
       $("#cekidalx").attr('disable', true);
       $("#cekidalx").html("NEXT");
       var musik = document.getElementById("musik");
        musik.play();
       
       if(playid == "" || playid == null || playid.length <= 6 || zoneid == "" || zoneid == null || zoneid.length <= 3)
       {
       $(".wrong").show();
       $(".respon").hide();
       $(".tidakada").hide();
       return false;
       }else{
       $(".wrong").hide();
       }
       
       $.ajax({
       type : 'POST',
       url : 'alxBackEnd/trueid.php?id='+playid+'&zone='+zoneid+'',
       beforeSend : function(){
             $(".respon").show();
             $("#cekidalx").attr('disable', true);
             $("#cekidalx").html("<i class='fa-solid fa-spinner fa-spin'></i>");
       },
       success : function(data){
       if(data == "" || data == null){
       $(".respon").hide();
       $(".tidakada").show();
       $("#cekidalx").attr('disable', false);
       $("#cekidalx").html("NEXT");
       }else{
       $(".AlexID").hide();
       $(".toast").show();
       $("#toastnick").text(data);
       $("input#ressplayid").val(playid);
       $("input#resszoneid").val(zoneid);
       $("input#ressnick").val(data);
       $("#trueidalx").fadeOut();
       setTimeout(() => {
       $(".backdrop").fadeOut();
       $(".toast").fadeOut();
       },2000)
       }
       }
       })
       
       }
</script>
